    <div className={isMobile ? 'App mobile' : 'App'}>
      {!shortUrl ?
      <Input
        input={input}
        setInput={setInput}
        getUrl={getUrl}
      /> :
      <Result
        input={input}
        setInput={setInput}
        shortUrl={shortUrl}
        setShortUrl={setShortUrl}
      />}
    </div>
  );
}

export default App;
