import { useState } from 'react';

const useBitly = () => {
    const [input, setInput] = useState('');
    const [shortUrl, setShortUrl] = useState('')

    const getUrl = (longUrl) => {
        const requestOptions = {
            method: 'POST',
            mode: 'cors',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${process.env.REACT_APP_API_TOKEN}`
            },
            body: JSON.stringify({
                domain: "bit.ly",
                long_url: longUrl
            })
        };

        if (longUrl.length > 0) {
            fetch('https://api-ssl.bitly.com/v4/shorten', requestOptions)
                .then(response => response.json())
                .then(data => {
                    setShortUrl(data?.link);
                    setInput('')
                    console.log('response data: ', data)
                })
                .catch(error => console.log('ERROR fetching shorten url: ', error));
        }

    }        

    return { getUrl, shortUrl, setShortUrl, input, setInput };
}

export default useBitly;
