import React, { useState } from 'react';
import { copy, copied } from '../assets';
import '../App.css';

const Result = (props) => {
  const { input, setInput, getUrl, response, handleCopy, animation, setAnimation, isDesktop } = props;

  return (
    <div className="Results">
      <input type="text" placeholder="Enter long URL..." value={input} onChange={e => setInput(e.target.value)} />
      <button className={`shorten-button ${isDesktop ? 'desktop' : 'mobile'}`} onClick={() => getUrl(input)}>Shorten URL</button>
        <div className="short-url" onClick={() => handleCopy()}>
          {console.log('response: ', response)}
          {response && (
            <>
              <p className={`response-text ${!response?.success && 'error'}`}>{response?.text}</p>
              {response?.success && <img className="copy" src={!animation ? copy : copied} alt="copy-icon" />}
            </>
          )}
        </div>
    </div>
  );
}

export default Result
