import React, { useState } from 'react';
import { copy, copied } from '../assets';
import '../App.css';

const Result = (props) => {
  const { input, setInput, getUrl, response, handleCopy, animation, setAnimation, isDesktop } = props;

  return (
    <div className="Results">
        <div className="short-url" onClick={() => handleCopy()}>
          <p className={`response-text ${!response?.success && 'error'}`}>{response?.text}</p>
          {response?.success && <img className="copy" src={!animation ? copy : copied} alt="copy-icon" />}
        </div>
    </div>
  );
}

export default Result;
