import React from 'react';
import { copy, copied } from '../assets';
import '../App.css';

const Result = (props) => {
  const { response, handleCopy, animation } = props;

  // a === true ? a : b === true ? b : c
  const responseText = response?.success ? 'short url' : !response?.success ? 'invalide input message' : 'nothing';
  console.log('responseText: ', responseText)

  return (
    <>
    {response?.success === true ? (
      <div className="results" onClick={() => handleCopy()}>
        <p className={`response-text`}>{response?.text}</p>
        <img className="copy" src={!animation ? copy : copied} alt="copy-icon" />
      </div>
    ) : response?.success === false ? (
      <div className="results" onClick={() => handleCopy()}>
        <p className={`response-text`}>Invalid input.  Please try a valid url.</p>
      </div>
    ) : (
      null
    )}
    </>
  );
}

export default Result;
