import React, { useState } from 'react';
import { copy, copied } from '../assets';
import '../App.css';

const Result = (props) => {
  const { input, setInput, getUrl, response, isDesktop } = props;
  const [animation, setAnimation] = useState(false);

  const handleCopy = () => {
    navigator.clipboard.writeText(response);
    setAnimation(true);
  }

  return (
    <div className="Results">
      <input type="text" placeholder="Enter long URL..." value={input} onChange={e => setInput(e.target.value)} />
      <button className={`shorten-button ${isDesktop ? 'desktop' : 'mobile'}`} onClick={() => getUrl(input)}>Shorten URL</button>
        <div className="short-url" onClick={() => handleCopy()}>
          {response?.success && (
            <>
              <p>{response?.text}</p>
              <img className="copy" src={!animation ? copy : copied} alt="copy-icon" />
            </>
          )}
        </div>
    </div>
  );
}

export default Result
