import { useState } from 'react';

const useBitly = () => {
    const [input, setInput] = useState('');
    // const [shortUrl, setShortUrl] = useState('')
    const [response, setResponse] = useState({
        success: false,
        text: ''
    })

    const getUrl = (longUrl) => {
        const requestOptions = {
            method: 'POST',
            mode: 'cors',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${process.env.REACT_APP_API_TOKEN}`
            },
            body: JSON.stringify({
                domain: "bit.ly",
                long_url: longUrl
            })
        };

        if (longUrl.length > 0) {
            fetch('https://api-ssl.bitly.com/v4/shorten', requestOptions)
                .then(response => response.json())
                .then(data => {
                    if (data?.errors?.length > 0 && data?.description) {
                        setResponse(data?.description)
                    } else {
                        setResponse(data?.link);
                    }
                    data?.error ? setResponse({ success: false, text: data?.description }) : setResponse({ success: true, text: data?.link })
                    // setInput('')
                })
                .catch(error => console.log('ERROR fetching shorten url: ', error));
        }

    }        

    return { getUrl, response, input, setInput };
}

export default useBitly;
